css:
body {
      border: 2px solid black;
      padding: 20px;
      margin: 0;
      font-family: Arial, sans-serif;
    }
    h3 {
      text-align: center;
      margin-top: 0;
    }
    table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 20px;
    }
    table, th, td {
      border: 1px solid black;
    }
    th, td {
      padding: 5px;
      text-align: center;
    }

    td[colspan='4'] {
      text-align: right;
      font-weight: bold;
    }
